import { FaBuilding, FaLocationArrow } from "react-icons/fa"; // Correct import for icons

function DoctorCard({ doctor }) {
  // Ensure doctor.specialties is an array, defaulting to an empty array if undefined
  const specialties = Array.isArray(doctor.specialties) ? doctor.specialties : [];
  
  // Extracting the required data from the doctor object
  const { name, fee, experience, clinic, location } = doctor;
  
  return (
    <div className="card mb-3" data-testid="doctor-card">
      <div className="card-body d-flex justify-content-between align-items-center">
        {/* Left side - Doctor's Details */}
        <div className="d-flex flex-column">
          <div className="d-flex align-items-center mb-2">
            {/* Doctor's Profile Pic */}
            <img 
              src={doctor.photo} 
              alt={name} 
              style={{ width: '50px', height: '50px', borderRadius: '50%' }} 
              className="mr-3" 
            />
            <h5 data-testid="doctor-name" className="font-weight-bold">{name}</h5>
          </div>
          {/* Specialty and Qualifications */}
          <p data-testid="doctor-specialty">{specialties.join(", ")}</p>
          <p data-testid="doctor-experience">{experience}</p>

          {/* Clinic and Location */}
          <div className="d-flex align-items-center mb-2">
            <FaBuilding className="mr-2" />
            <p className="mb-0">{clinic?.name}</p>
          </div>
          <div className="d-flex align-items-center">
            <FaLocationArrow className="mr-2" />
            <p className="mb-0">{clinic?.address?.locality}, {clinic?.address?.city}</p>
          </div>
        </div>

        {/* Right side - Fee and Book Appointment Button */}
        <div className="text-end">
          <p data-testid="doctor-fee" className="mb-2">â‚¹ {fee}</p>
          <button 
            className="btn btn-outline-primary" 
            style={{
              borderColor: '#007bff',
              color: '#007bff',
            }}
            onMouseOver={(e) => {
              e.target.style.backgroundColor = '#007bff';
              e.target.style.color = 'white';
            }}
            onMouseOut={(e) => {
              e.target.style.backgroundColor = 'white';
              e.target.style.color = '#007bff';
            }}
          >
            Book Appointment
          </button>
        </div>
      </div>
    </div>
  );
}

export default DoctorCard;
