import { useEffect, useState } from "react";
import { API_URL } from "./constants";
import DoctorCard from "./components/DoctorCard";
import SearchBar from "./components/SearchBar";
import FilterPanel from "./components/FilterPanel";  // You can define this panel later
import SortPanel from "./components/SortPanel";  // You can define this panel later

function App() {
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredDoctors, setFilteredDoctors] = useState([]);

  useEffect(() => {
    fetch(API_URL)
      .then((res) => res.json())
      .then((data) => {
        setDoctors(data);
        setFilteredDoctors(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching data:", err);
        setLoading(false);
      });
  }, []);

  const handleSearchChange = (event) => {
    const query = event.target.value;
    setSearchQuery(query);

    const filtered = doctors.filter((doctor) =>
      doctor.name.toLowerCase().includes(query.toLowerCase())
    );

    setFilteredDoctors(filtered);
  };

  return (
    <div className="container mt-4">
      <div className="d-flex">
        {/* Left Panel - Filter and Sort */}
        <div className="d-flex flex-column" style={{ width: '300px' }}>
          <FilterPanel />
          <SortPanel />
        </div>

        {/* Right Panel - Doctor List */}
        <div className="ms-4 flex-grow-1">
          {/* Search Bar Component */}
          <SearchBar
            searchQuery={searchQuery}
            onSearchChange={handleSearchChange}
          />

          {loading ? (
            <p>Loading doctors...</p>
          ) : (
            <>
              {filteredDoctors.map((doc, i) => (
                <DoctorCard key={i} doctor={doc} />
              ))}
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
