import React, { useState } from "react";
import FilterPanel from "./components/FilterPanel";
import DoctorCard from "./components/DoctorCard";
import SortPanel from "./components/SortPanel";

// Sample doctor data (this can be replaced by actual API data)
const doctors = [
  {
    id: 1,
    name: "Dr. Kshitija Jagdale",
    photo: "https://doctorlistingingestionpr.azureedge.net/539482078762581145_5a00f31266ed11efbae40ada1afa5198_ProfilePic_crop%20pic.jpg",
    consultationType: "In Clinic",
    specialities: ["Dentist"],
    fees: 500,
    experience: 13,
    clinic: { name: "The Dent Inn Advanced Dental Clinic", address: { locality: "Wanowrie" } }
  },
  // Add other doctors here
];

function App() {
  const [consultationType, setConsultationType] = useState("All");
  const [specialties, setSpecialties] = useState([]);
  const [sortOption, setSortOption] = useState("Price");

  const handleConsultationTypeChange = (e) => {
    setConsultationType(e.target.value);
  };

  const handleSpecialtyChange = (specialty) => {
    setSpecialties((prevSpecialties) =>
      prevSpecialties.includes(specialty)
        ? prevSpecialties.filter((spec) => spec !== specialty)
        : [...prevSpecialties, specialty]
    );
  };

  const handleSortOptionChange = (e) => {
    setSortOption(e.target.value);
  };

  const handleClearFilters = () => {
    setConsultationType("All");
    setSpecialties([]);
  };

  return (
    <div className="container mt-4">
      <div className="d-flex">
        <FilterPanel
          consultationType={consultationType}
          onConsultationTypeChange={handleConsultationTypeChange}
          specialties={specialties}
          onSpecialtyChange={handleSpecialtyChange}
          onClearFilters={handleClearFilters}
        />
        <div className="doctor-list">
          {doctors
            .filter(
              (doctor) =>
                (consultationType === "All" ||
                  doctor.consultationType === consultationType) &&
                (specialties.length === 0 ||
                  doctor.specialities.some((specialty) =>
                    specialties.includes(specialty)
                  ))
            )
            .sort((a, b) => {
              if (sortOption === "Price") {
                return a.fees - b.fees;
              } else if (sortOption === "Experience") {
                return b.experience - a.experience;
              }
              return 0;
            })
            .map((doctor) => (
              <DoctorCard key={doctor.id} doctor={doctor} />
            ))}
        </div>
      </div>
    </div>
  );
}

export default App;
