import React, { useState, useEffect } from "react";
import { FaSearch } from "react-icons/fa";
import SortPanel from "./SortPanel"; // Import SortPanel here


function FilterPanel({
  consultationType,
  onConsultationTypeChange,
  doctors,  // List of doctors passed as a prop
  onSpecialtyChange,
  onClearFilters
}) {
  const [searchQuery, setSearchQuery] = useState("");
  const [uniqueSpecialties, setUniqueSpecialties] = useState([]);

  // Extract unique specialties from the API response
  useEffect(() => {
    const allSpecialties = doctors
      .flatMap((doctor) => doctor.specialities.map((spec) => spec.name)); // Flatten the list of specialties
    const uniqueSpecs = [...new Set(allSpecialties)]; // Remove duplicates using Set
    setUniqueSpecialties(uniqueSpecs);
  }, [doctors]); // Run this whenever doctors list is updated

  const filteredSpecialties = uniqueSpecialties.filter((specialty) =>
    specialty.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="filter-panel">
      <div className="filter-header" id="filter-header-sort">
        <h5>Sort By</h5>
      </div>
      <SortPanel /> {/* Include the Sort Panel here */}

      {/* Specialties Panel */}
      <div className="filter-header" id="filter-header-speciality">
        <h5>Specialties</h5>
        <input
          type="text"
          placeholder="Search Specialties"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="specialty-search-bar"
          data-testid="filter-specialty-search"
        />
        <div className="checkbox-list">
          {filteredSpecialties.map((specialty) => (
            <label key={specialty}>
              <input
                type="checkbox"
                value={specialty}
                onChange={() => onSpecialtyChange(specialty)}
                data-testid={`filter-specialty-${specialty.replace(/\s+/g, "-")}`}
              />
              {specialty}
            </label>
          ))}
        </div>
      </div>

      {/* Consultation Mode Panel */}
      <div className="filter-header" id="filter-header-moc">
        <h5>Consultation Mode</h5>
        <div>
          <label>
            <input
              type="radio"
              name="consultation"
              value="Video Consult"
              checked={consultationType === "Video Consult"}
              onChange={onConsultationTypeChange}
              data-testid="filter-video-consult"
            />
            Video Consult
          </label>
          <label>
            <input
              type="radio"
              name="consultation"
              value="In Clinic"
              checked={consultationType === "In Clinic"}
              onChange={onConsultationTypeChange}
              data-testid="filter-in-clinic"
            />
            In Clinic
          </label>
          <label>
            <input
              type="radio"
              name="consultation"
              value="All"
              checked={consultationType === "All"}
              onChange={onConsultationTypeChange}
              data-testid="filter-all-consultation"
            />
            All
          </label>
        </div>
      </div>

      <button className="btn btn-link" onClick={onClearFilters}>
        Clear All Filters
      </button>
    </div>
  );
}

export default FilterPanel;
